import React, { useState, useRef, useEffect } from 'react';
import Header from './components/Header';
import Visualizer from './components/Visualizer';
import Information from './components/Information';
import Footer from './components/Footer';
import { Machine, Link } from './types';
import './App.css';

const App: React.FC = () => {
  const [selectedMachine, setSelectedMachine] = useState<Machine | null>(null);
  const visualizerRef = useRef<HTMLDivElement>(null);

  // Exemple de données pour 5 machines
  const machines: Machine[] = [
    { id: '1', name: '1', ip: 'xxx.xxx.xxx.xxx', mac: 'xx:xx:xx:xx:xx:xx', lastUpdate: '01/03/2025' },
    { id: '2', name: '2', ip: 'xxx.xxx.xxx.xxx', mac: 'xx:xx:xx:xx:xx:xx', lastUpdate: '28/02/2025' },
    { id: '3', name: '3', ip: 'xxx.xxx.xxx.xxx', mac: 'xx:xx:xx:xx:xx:xx', lastUpdate: '02/03/2025' },
    { id: '4', name: '4', ip: 'xxx.xxx.xxx.xxx', mac: 'xx:xx:xx:xx:xx:xx', lastUpdate: '03/03/2025' },
    { id: '5', name: '5', ip: 'xxx.xxx.xxx.xxx', mac: 'xx:xx:xx:xx:xx:xx', lastUpdate: '04/03/2025' },
  ];

  // Connexions entre toutes les machines (graphe complet)
  const links: Link[] = [
    { source: '1', target: '2' },
    { source: '1', target: '3' },
    { source: '1', target: '4' },
    { source: '1', target: '5' },
    { source: '2', target: '3' },
    { source: '2', target: '4' },
    { source: '2', target: '5' },
    { source: '3', target: '4' },
    { source: '3', target: '5' },
    { source: '4', target: '5' },
  ];

  const handleMachineClick = (machine: Machine) => {
    setSelectedMachine(machine);
  };

  const handleBackgroundClick = (event: React.MouseEvent<HTMLDivElement>) => {
    if (event.currentTarget === event.target) {
      setSelectedMachine(null);
    }
  };

  // Event listener pour les clics sur le document
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        visualizerRef.current && 
        !visualizerRef.current.contains(event.target as Node) &&
        selectedMachine !== null
      ) {
        setSelectedMachine(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [selectedMachine]);

  return (
    <div className="app">
      <Header />
      <div className="main-content">
        {selectedMachine && (
          <Information machine={selectedMachine} />
        )}
        <div 
          ref={visualizerRef} 
          onClick={handleBackgroundClick} 
          style={{ flex: 1, height: '100%' }}
        >
          <Visualizer 
            machines={machines} 
            links={links} 
            onMachineClick={handleMachineClick}
            isReduced={selectedMachine !== null}
          />
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default App;
