import React from 'react';
import { Machine as MachineType } from '../types';
import './Machine.css';

interface MachineProps {
    machine: MachineType;
    x: number;
    y: number;
    onClick: () => void;
}

const Machine: React.FC<MachineProps> = ({ machine, x, y, onClick }) => {
    return (
        <g className="machine" onClick={onClick} transform={`translate(${x}, ${y})`}>
            {/* Réduction de la taille du cercle de 40 à 30 */}
            <circle className="machine-circle" cx="0" cy="0" r="30" />
            <text className="machine-text" x="0" y="0" textAnchor="middle" dominantBaseline="central">
                {machine.name}
            </text>
        </g>
    );
};

export default Machine;
