import React, { useState, useRef, useEffect, useCallback } from 'react';
import Machine from './Machine';
import MachineLink from './MachineLink';
import { Machine as MachineType, Link } from '../types';
import './Visualizer.css';

interface VisualizerProps {
    machines: MachineType[];
    links: Link[];
    onMachineClick: (machine: MachineType) => void;
    isReduced: boolean;
    initialPositions?: Record<string, Position>;
    onPositionsChange?: (positions: Record<string, Position>) => void;
}

interface Position {
    x: number;
    y: number;
}

const Visualizer: React.FC<VisualizerProps> = ({ 
    machines, 
    links, 
    onMachineClick,
    isReduced,
    initialPositions = {},
    onPositionsChange
}) => {
    const svgRef = useRef<SVGSVGElement>(null);
    const [positions, setPositions] = useState<Record<string, Position>>({});
    const [circlePositions, setCirclePositions] = useState<Record<string, Position>>({});
    const [dragging, setDragging] = useState<string | null>(null);
    const [dragStart, setDragStart] = useState<Position>({ x: 0, y: 0 });
    const [viewBox, setViewBox] = useState({ x: 0, y: 0, width: 550, height: 350 });
    const [isPanning, setIsPanning] = useState(false);
    const [lastPosition, setLastPosition] = useState({ x: 0, y: 0 });
    const [scale, setScale] = useState(1);

    const calculateCirclePositions = useCallback(() => {
        const centerX = 275;
        const centerY = 175;
        const radius = 100;

        const newCirclePositions: Record<string, Position> = {};
        machines.forEach((machine, index) => {
            const angle = (index * 2 * Math.PI) / machines.length;
            newCirclePositions[machine.id] = {
                x: centerX + radius * Math.cos(angle),
                y: centerY + radius * Math.sin(angle)
            };
        });

        return newCirclePositions;
    }, [machines]);

    useEffect(() => {
        const newCirclePositions = calculateCirclePositions();
        setCirclePositions(newCirclePositions);
    }, [calculateCirclePositions]);

    // Initialize machine positions from props or in a circle layout
    useEffect(() => {
        const newCirclePositions = calculateCirclePositions();

        if (Object.keys(initialPositions).length > 0) {
            // Use provided positions if available
            const newPositions: Record<string, Position> = {};

            machines.forEach(machine => {
                if (initialPositions[machine.id]) {
                    newPositions[machine.id] = initialPositions[machine.id];
                } else {
                    // Fallback to circle position for new machines
                    newPositions[machine.id] = newCirclePositions[machine.id];
                }
            });

            setPositions(newPositions);
            setCirclePositions(newCirclePositions);
        } else {
            // Use circle layout if no positions provided
            setPositions(newCirclePositions);
            setCirclePositions(newCirclePositions);
        }
    }, [machines, initialPositions, calculateCirclePositions]);

    // Notify parent component when positions change
    useEffect(() => {
        if (onPositionsChange && Object.keys(positions).length > 0) {
            onPositionsChange(positions);
        }
    }, [positions, onPositionsChange]);

    // Handle mouse down on SVG for panning
    const handleSvgMouseDown = (e: React.MouseEvent) => {
        if (e.button === 0 && !dragging) { // Left click and not dragging a machine
            setIsPanning(true);
            setLastPosition({ x: e.clientX, y: e.clientY });
        }
    };

    // Handle mouse move for panning and dragging
    const handleSvgMouseMove = (e: React.MouseEvent) => {
        if (dragging) {
            // We're dragging a machine - use a fixed reference point to avoid trembling
            const svg = svgRef.current;
            if (!svg) return;

            const point = svg.createSVGPoint();
            point.x = e.clientX;
            point.y = e.clientY;

            // Get current transformation matrix
            const ctm = svg.getScreenCTM();
            if (!ctm) return;

            // Transform client coordinates to SVG coordinates
            const svgPoint = point.matrixTransform(ctm.inverse());

            // Calculate movement delta from the start position
            const dx = svgPoint.x - dragStart.x;
            const dy = svgPoint.y - dragStart.y;

            // Update machine position based on its original position plus the delta
            const originalPos = positions[dragging];

            setPositions(prev => ({
                ...prev,
                [dragging]: {
                    x: smoothMove(prev[dragging].x, originalPos.x + dx),
                    y: smoothMove(prev[dragging].y, originalPos.y + dy)
                }
            }));            
        } else if (isPanning) {
            // We're panning the view
            const dx = e.clientX - lastPosition.x;
            const dy = e.clientY - lastPosition.y;

            // Adjust based on current scale
            const panSpeed = 1 / scale;

            setViewBox(prev => ({
                ...prev,
                x: prev.x - dx * panSpeed,
                y: prev.y - dy * panSpeed
            }));

            setLastPosition({ x: e.clientX, y: e.clientY });
        }
    };

    // Handle mouse up for ending panning and dragging
    const handleSvgMouseUp = () => {
        if (dragging) {
            // Update the original position for next drag operation
            const svg = svgRef.current;
            if (svg) {
                const ctm = svg.getScreenCTM();
                if (ctm) {
                    // Store the new position as the base for next drag
                    const originalPos = positions[dragging];

                    setPositions(prev => ({
                        ...prev,
                        [dragging]: {
                            x: originalPos.x,
                            y: originalPos.y
                        }
                    }));
                }
            }
        }

        setIsPanning(false);
        setDragging(null);
    };

    // Handle mouse leave for ending panning and dragging
    const handleSvgMouseLeave = () => {
        setIsPanning(false);
        setDragging(null);
    };

    // Handle machine drag start
    const handleMachineDragStart = (id: string, e: React.MouseEvent) => {
        e.stopPropagation(); // Prevent SVG from starting a pan operation

        const svg = svgRef.current;
        if (!svg) return;

        const point = svg.createSVGPoint();
        point.x = e.clientX;
        point.y = e.clientY;

        // Get current transformation matrix
        const ctm = svg.getScreenCTM();
        if (!ctm) return;

        // Transform client coordinates to SVG coordinates
        const svgPoint = point.matrixTransform(ctm.inverse());

        // Store the start position for calculating movement delta
        setDragStart({
            x: svgPoint.x,
            y: svgPoint.y
        });

        // Store a copy of the current positions to use as reference
        const originalPos = positions[id];
        setPositions(prev => ({
            ...prev,
            [id]: {
                x: originalPos.x,
                y: originalPos.y
            }
        }));

        setDragging(id);
    };

    // Handle zoom with mouse wheel
    const handleWheel = (e: React.WheelEvent) => {
        e.preventDefault();

        const svg = svgRef.current;
        if (!svg) return;

        const point = svg.createSVGPoint();
        point.x = e.clientX;
        point.y = e.clientY;

        // Get current transformation matrix
        const ctm = svg.getScreenCTM();
        if (!ctm) return;

        // Transform client coordinates to SVG coordinates
        const svgPoint = point.matrixTransform(ctm.inverse());

        // Calculate new scale
        const zoomFactor = e.deltaY > 0 ? 1.1 : 0.9;
        const newScale = Math.max(0.5, Math.min(5, scale * zoomFactor));

        // Calculate new viewBox dimensions
        const newWidth = viewBox.width * (newScale / scale);
        const newHeight = viewBox.height * (newScale / scale);

        // Calculate new viewBox position to zoom toward/away from mouse
        const mouseXRatio = (svgPoint.x - viewBox.x) / viewBox.width;
        const mouseYRatio = (svgPoint.y - viewBox.y) / viewBox.height;

        const newX = svgPoint.x - mouseXRatio * newWidth;
        const newY = svgPoint.y - mouseYRatio * newHeight;

        setViewBox({
            x: newX,
            y: newY,
            width: newWidth,
            height: newHeight
        });

        setScale(newScale);
    };

    // Handle zoom in button
    const handleZoomIn = () => {
        setScale(prev => {
            const newScale = Math.min(5, prev * 1.2);

            // Adjust viewBox to maintain center
            setViewBox(prev => {
                const centerX = prev.x + prev.width / 2;
                const centerY = prev.y + prev.height / 2;

                const newWidth = prev.width * (scale / newScale);
                const newHeight = prev.height * (scale / newScale);

                return {
                    x: centerX - newWidth / 2,
                    y: centerY - newHeight / 2,
                    width: newWidth,
                    height: newHeight
                };
            });

            return newScale;
        });
    };

    // Handle zoom out button
    const handleZoomOut = () => {
        setScale(prev => {
            const newScale = Math.max(0.5, prev / 1.2);

            // Adjust viewBox to maintain center
            setViewBox(prev => {
                const centerX = prev.x + prev.width / 2;
                const centerY = prev.y + prev.height / 2;

                const newWidth = prev.width * (scale / newScale);
                const newHeight = prev.height * (scale / newScale);

                return {
                    x: centerX - newWidth / 2,
                    y: centerY - newHeight / 2,
                    width: newWidth,
                    height: newHeight
                };
            });

            return newScale;
        });
    };

    // Reset machine positions to initial circle layout in one go
    const handleResetPositions = () => {
        // Calculate fresh circle positions to ensure consistency
        const freshCirclePositions = calculateCirclePositions();
        setPositions(freshCirclePositions);
        setCirclePositions(freshCirclePositions);

        // Also update in localStorage via the callback
        if (onPositionsChange) {
            onPositionsChange(freshCirclePositions);
        }
    };

    // Compute viewBox string
    const viewBoxString = `${viewBox.x} ${viewBox.y} ${viewBox.width} ${viewBox.height}`;

    return (
        <div className={`visualizer ${isReduced ? 'reduced' : ''}`}>
            <svg 
                ref={svgRef}
                width="100%" 
                height="100%" 
                viewBox={viewBoxString}
                onMouseDown={handleSvgMouseDown}
                onMouseMove={handleSvgMouseMove}
                onMouseUp={handleSvgMouseUp}
                onMouseLeave={handleSvgMouseLeave}
                onWheel={handleWheel}
            >
                {links.map((link, index) => {
                    // Only render links if we have positions for both machines
                    if (!positions[link.source] || !positions[link.target]) return null;

                    return (
                        <MachineLink
                            key={index}
                            sourceX={positions[link.source].x}
                            sourceY={positions[link.source].y}
                            targetX={positions[link.target].x}
                            targetY={positions[link.target].y}
                            type={link.type}
                        />
                    );
                })}

                {machines.map(machine => {
                    // Only render machines if we have positions for them
                    if (!positions[machine.id]) return null;

                    return (
                        <g 
                            key={machine.id}
                            onMouseDown={(e) => handleMachineDragStart(machine.id, e)}
                        >
                            <Machine
                                machine={machine}
                                x={positions[machine.id].x}
                                y={positions[machine.id].y}
                                onClick={() => onMachineClick(machine)}
                            />
                        </g>
                    );
                })}
            </svg>

            {/* Zoom controls */}
            <div className="visualizer-controls">
                <button 
                    onClick={handleZoomIn}
                    className="zoom-button"
                    title="Zoom In"
                >
                    +
                </button>
                <button 
                    onClick={handleZoomOut}
                    className="zoom-button"
                    title="Zoom Out"
                >
                    -
                </button>
                <button 
                    onClick={handleResetPositions}
                    className="zoom-button reset-button"
                    title="Reset Positions"
                >
                    Reset
                </button>
            </div>
        </div>
    );
};

export default Visualizer;
