import React from 'react';
import { Machine } from '../types';
import './Information.css';

interface InformationProps {
    machine: Machine;
}

const Information: React.FC<InformationProps> = ({ machine }) => {
    return (
        <div className="information">
            <h2>{machine.name}</h2>
            <div className="info-line">
                <span className="info-label">ID :</span> 
                <span className="info-value">{machine.id.padStart(7, 'x')}</span>
            </div>
            <div className="info-line">
                <span className="info-label">IP :</span> 
                <span className="info-value">{machine.ip}</span>
            </div>
            {machine.mac && (
                <div className="info-line">
                    <span className="info-label">MAC :</span> 
                    <span className="info-value">{machine.mac}</span>
                </div>
            )}
            <div className="info-line">
                <span className="info-label">Last update :</span> 
                <span className="info-value">{machine.lastUpdate}</span>
            </div>
        </div>
    );
};

export default Information;
